CREATE TABLE `SensorData` (`id` INT(6) PRIMARY KEY AUTO_INCREMENT NOT NULL, `typ_sensoru` VARCHAR(30) NOT NULL, `umisteni` VARCHAR(30) NOT NULL, `teplota` DECIMAL(6,2) NOT NULL, `vlhkost` DECIMAL(6,2) NOT NULL, `atm_tlak` DECIMAL(6,2) NOT NULL, `n_vyska` DECIMAL(6,0) NOT NULL, `cas_zapisu` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP()) DEFAULT CHARACTER SET='utf8';
